import React, {useState} from 'react';
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpenseItems from "./ExpenseItems";
import './Expenses.css'

function Expenses(props){
    const [year, setYear]=useState('2021');
    const yearSelectionHandler=(selectedYear)=>{
        //const year=selectedYear;
        setYear(selectedYear);
        console.log(selectedYear);

    }

    return (
      <div>

        <Card className='expenses'>
        <ExpensesFilter setDefault={year}  onYearSelection={yearSelectionHandler}/>
        {props.items.map((expense)=>(<ExpenseItems  title={expense.title} amount={expense.amount} date={expense.date}/>))}

      </Card>

      </div>
    );
}
export default Expenses;
